#!/usr/bin/env python3

dna = ':'

ecoStart = 'GAATTC'

#  5' -  G | AATTC
#  3' -  C   TTAA | G

cutPos = dna.find(ecoStart)
print(cutPos)
print('The EcoRI enzyme will cut between base' , (cutPos + 1) , 'and base' , (cutPos + 2))
print('The end of the EcoRI cut site is between base' , (cutPos + 6) , 'and base' , (cutPos + 7))

print('EcoRI start position:' , (cutPos + 1) , 'and end position:' , (cutPos + 6))

print('The DNA sequence that matches to this position is:' , dna[cutPos:(cutPos + 6)])

